import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { trpc } from '@/utils/trpc';
import { useState, useEffect, useCallback } from 'react';
import type { Counter } from '../../server/src/schema';

function App() {
  const [counter, setCounter] = useState<Counter | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  // Load initial counter value
  const loadCounter = useCallback(async () => {
    try {
      const result = await trpc.getCounter.query();
      setCounter(result);
    } catch (error) {
      console.error('Failed to load counter:', error);
    }
  }, []);

  useEffect(() => {
    loadCounter();
  }, [loadCounter]);

  const handleIncrement = async () => {
    setIsLoading(true);
    try {
      const result = await trpc.incrementCounter.mutate({
        increment: 1
      });
      setCounter(result);
    } catch (error) {
      console.error('Failed to increment counter:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-full max-w-md shadow-lg">
        <CardHeader className="text-center">
          <CardTitle className="text-2xl font-bold text-gray-800">
            🎯 Simple Counter ✨
          </CardTitle>
        </CardHeader>
        <CardContent className="text-center space-y-6">
          <div className="space-y-2">
            <div className="text-6xl font-bold text-indigo-600">
              {counter?.value ?? '...'}
            </div>
            <p className="text-sm text-gray-500">
              Current Count
            </p>
          </div>

          <Button 
            onClick={handleIncrement}
            disabled={isLoading}
            className="w-full h-12 text-lg font-semibold bg-indigo-600 hover:bg-indigo-700 transition-colors"
          >
            {isLoading ? (
              <span className="flex items-center gap-2">
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                Counting...
              </span>
            ) : (
              '➕ Click to Increment 🎉'
            )}
          </Button>

          {counter?.updated_at && (
            <p className="text-xs text-gray-400">
              Last updated: {counter.updated_at.toLocaleString()}
            </p>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default App;

