<file path="client/src/App.tsx">
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { trpc } from '@/utils/trpc';
import { useState, useEffect, useCallback } from 'react';
import type { Counter } from '../../server/src/schema';

function App() {
  const [counter, setCounter] = useState<Counter | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isIncrementing, setIsIncrementing] = useState(false);

  // Load counter data on mount
  const loadCounter = useCallback(async () => {
    setIsLoading(true);
    try {
      const result = await trpc.getCounter.query();
      setCounter(result);
    } catch (error) {
      console.error('Failed to load counter:', error);
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadCounter();
  }, [loadCounter]);

  const handleIncrement = async () => {
    setIsIncrementing(true);
    try {
      const result = await trpc.incrementCounter.mutate({ increment: 1 });
      setCounter(result);
    } catch (error) {
      console.error('Failed to increment counter:', error);
    } finally {
      setIsIncrementing(false);
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="p-8 text-center">
            <div className="text-lg text-gray-600">Loading counter...</div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-96 shadow-xl">
        <CardHeader className="text-center">
          <CardTitle className="text-3xl font-bold text-gray-800">
            🔢 Counter App
          </CardTitle>
        </CardHeader>
        <CardContent className="p-8 text-center space-y-6">
          <div className="space-y-2">
            <div className="text-6xl font-bold text-indigo-600">
              {counter?.value ?? 0}
            </div>
            <div className="text-sm text-gray-500">
              Current Count
            </div>
          </div>

          <Button
            onClick={handleIncrement}
            disabled={isIncrementing}
            size="lg"
            className="w-full text-lg py-6 bg-indigo-600 hover:bg-indigo-700 transition-colors"
          >
            {isIncrementing ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                Incrementing...
              </>
            ) : (
              <>
                ➕ Click to Increment
              </>
            )}
          </Button>

          {counter && (
            <div className="text-xs text-gray-400 border-t pt-4">
              Last updated: {counter.updated_at.toLocaleString()}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default App;
</file>

<file path="server/src/db/schema.ts">
import { serial, integer, pgTable, timestamp } from 'drizzle-orm/pg-core';

export const countersTable = pgTable('counters', {
  id: serial('id').primaryKey(),
  value: integer('value').notNull().default(0),
  updated_at: timestamp('updated_at').defaultNow().notNull(),
});

// TypeScript type for the table schema
export type Counter = typeof countersTable.$inferSelect;
export type NewCounter = typeof countersTable.$inferInsert;

// Export all tables for proper query building
export const tables = { counters: countersTable };
</file>

<file path="server/src/handlers/get_counter.ts">
import { db } from '../db';
import { countersTable } from '../db/schema';
import { type Counter } from '../schema';

export const getCounter = async (): Promise<Counter> => {
  try {
    // Get the first counter record, or create one if none exists
    const results = await db.select()
      .from(countersTable)
      .limit(1)
      .execute();

    if (results.length === 0) {
      // Create initial counter if none exists
      const newCounter = await db.insert(countersTable)
        .values({
          value: 0
        })
        .returning()
        .execute();

      return newCounter[0];
    }

    return results[0];
  } catch (error) {
    console.error('Get counter failed:', error);
    throw error;
  }
};
</file>

<file path="server/src/handlers/increment_counter.ts">
import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput, type Counter } from '../schema';
import { eq, sql } from 'drizzle-orm';

export const incrementCounter = async (input: IncrementCounterInput): Promise<Counter> => {
  try {
    // First, check if a counter exists (assuming we want a single global counter)
    const existingCounters = await db.select()
      .from(countersTable)
      .limit(1)
      .execute();

    let result;

    if (existingCounters.length === 0) {
      // Create new counter with the increment value
      result = await db.insert(countersTable)
        .values({
          value: input.increment
        })
        .returning()
        .execute();
    } else {
      // Update existing counter by incrementing its value
      result = await db.update(countersTable)
        .set({
          value: sql`${countersTable.value} + ${input.increment}`,
          updated_at: sql`now()`
        })
        .where(eq(countersTable.id, existingCounters[0].id))
        .returning()
        .execute();
    }

    return result[0];
  } catch (error) {
    console.error('Counter increment failed:', error);
    throw error;
  }
};
</file>

<file path="server/src/index.ts">
import { initTRPC } from '@trpc/server';
import { createHTTPServer } from '@trpc/server/adapters/standalone';
import 'dotenv/config';
import cors from 'cors';
import superjson from 'superjson';
import { incrementCounterInputSchema } from './schema';
import { getCounter } from './handlers/get_counter';
import { incrementCounter } from './handlers/increment_counter';

const t = initTRPC.create({
  transformer: superjson,
});

const publicProcedure = t.procedure;
const router = t.router;

const appRouter = router({
  healthcheck: publicProcedure.query(() => {
    return { status: 'ok', timestamp: new Date().toISOString() };
  }),
  getCounter: publicProcedure
    .query(() => getCounter()),
  incrementCounter: publicProcedure
    .input(incrementCounterInputSchema)
    .mutation(({ input }) => incrementCounter(input)),
});

export type AppRouter = typeof appRouter;

async function start() {
  const port = process.env['SERVER_PORT'] || 2022;
  const server = createHTTPServer({
    middleware: (req, res, next) => {
      cors()(req, res, next);
    },
    router: appRouter,
    createContext() {
      return {};
    },
  });
  server.listen(port);
  console.log(`TRPC server listening at port: ${port}`);
}

start();
</file>

<file path="server/src/schema.ts">
import { z } from 'zod';

// Counter schema
export const counterSchema = z.object({
  id: z.number(),
  value: z.number().int(),
  updated_at: z.coerce.date()
});

export type Counter = z.infer<typeof counterSchema>;

// Input schema for incrementing counter
export const incrementCounterInputSchema = z.object({
  increment: z.number().int().positive().default(1)
});

export type IncrementCounterInput = z.infer<typeof incrementCounterInputSchema>;
</file>

