import { db } from '../db';
import { countersTable } from '../db/schema';
import { type Counter } from '../schema';

export const getCounter = async (): Promise<Counter> => {
  try {
    // Get the first counter from the database
    const result = await db.select()
      .from(countersTable)
      .limit(1)
      .execute();

    // If no counter exists, create one with default value
    if (result.length === 0) {
      const newCounter = await db.insert(countersTable)
        .values({
          value: 0
        })
        .returning()
        .execute();

      return newCounter[0];
    }

    return result[0];
  } catch (error) {
    console.error('Counter retrieval failed:', error);
    throw error;
  }
};
