import { afterEach, beforeEach, describe, expect, it } from 'bun:test';
import { resetDB, createDB } from '../helpers';
import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput } from '../schema';
import { incrementCounter } from '../handlers/increment_counter';

// Test inputs
const defaultIncrement: IncrementCounterInput = {
  increment: 1
};

const customIncrement: IncrementCounterInput = {
  increment: 5
};

describe('incrementCounter', () => {
  beforeEach(createDB);
  afterEach(resetDB);

  it('should create a new counter when none exists', async () => {
    const result = await incrementCounter(defaultIncrement);

    // Basic field validation
    expect(result.id).toBeDefined();
    expect(result.value).toEqual(1);
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should increment existing counter', async () => {
    // Create initial counter
    await incrementCounter(defaultIncrement);

    // Increment it again
    const result = await incrementCounter(customIncrement);

    expect(result.value).toEqual(6); // 1 + 5
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should save counter to database correctly', async () => {
    const result = await incrementCounter(customIncrement);

    // Verify data is saved in database
    const counters = await db.select()
      .from(countersTable)
      .execute();

    expect(counters).toHaveLength(1);
    expect(counters[0].id).toEqual(result.id);
    expect(counters[0].value).toEqual(5);
    expect(counters[0].updated_at).toBeInstanceOf(Date);
  });

  it('should handle multiple increments correctly', async () => {
    // First increment
    await incrementCounter({ increment: 3 });

    // Second increment
    await incrementCounter({ increment: 7 });

    // Third increment
    const result = await incrementCounter({ increment: 2 });

    expect(result.value).toEqual(12); // 3 + 7 + 2

    // Verify only one counter exists
    const counters = await db.select()
      .from(countersTable)
      .execute();

    expect(counters).toHaveLength(1);
    expect(counters[0].value).toEqual(12);
  });

  it('should update timestamp on each increment', async () => {
    // First increment
    const first = await incrementCounter(defaultIncrement);
    const firstTime = first.updated_at;

    // Wait a small amount to ensure timestamp difference
    await new Promise(resolve => setTimeout(resolve, 10));

    // Second increment
    const second = await incrementCounter(defaultIncrement);
    const secondTime = second.updated_at;

    expect(secondTime > firstTime).toBe(true);
    expect(second.value).toEqual(2);
  });
});
