import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { trpc } from '@/utils/trpc';
import { useState, useEffect, useCallback } from 'react';
import type { Counter } from '../../server/src/schema';

function App() {
  const [counter, setCounter] = useState<Counter | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isIncrementLoading, setIsIncrementLoading] = useState(false);

  const loadCounter = useCallback(async () => {
    setIsLoading(true);
    try {
      const result = await trpc.getCounter.query();
      setCounter(result);
    } catch (error) {
      console.error('Failed to load counter:', error);
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadCounter();
  }, [loadCounter]);

  const handleIncrement = async () => {
    setIsIncrementLoading(true);
    try {
      const result = await trpc.incrementCounter.mutate({ increment: 1 });
      setCounter(result);
    } catch (error) {
      console.error('Failed to increment counter:', error);
    } finally {
      setIsIncrementLoading(false);
    }
  };

  if (isLoading || !counter) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="p-8">
            <div className="flex items-center justify-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-96 shadow-xl">
        <CardHeader className="text-center pb-4">
          <CardTitle className="text-3xl font-bold text-gray-800">
            🎯 Click Counter
          </CardTitle>
          <CardDescription className="text-gray-600">
            ✨ Click the button to increment the counter!
          </CardDescription>
        </CardHeader>
        <CardContent className="text-center space-y-6">
          <div className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white rounded-lg p-6">
            <div className="text-sm font-medium opacity-90 mb-1">Current Count</div>
            <div className="text-4xl font-bold">{counter.value}</div>
          </div>

          <Button
            onClick={handleIncrement}
            disabled={isIncrementLoading}
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors duration-200"
            size="lg"
          >
            {isIncrementLoading ? (
              <div className="flex items-center gap-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                Counting...
              </div>
            ) : (
              <div className="flex items-center gap-2">
                <span>🚀</span>
                Click Me!
              </div>
            )}
          </Button>

          <div className="text-xs text-gray-500 pt-2">
            Last updated: {counter.updated_at.toLocaleString()}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

export default App;

