import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { trpc } from '@/utils/trpc';
import { useState, useEffect, useCallback } from 'react';
import type { Counter } from '../../server/src/schema';

function App() {
  const [counter, setCounter] = useState<Counter | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isInitialLoading, setIsInitialLoading] = useState(true);

  // Load counter data on mount
  const loadCounter = useCallback(async () => {
    try {
      const result = await trpc.getCounter.query();
      setCounter(result);
    } catch (error) {
      console.error('Failed to load counter:', error);
    } finally {
      setIsInitialLoading(false);
    }
  }, []);

  useEffect(() => {
    loadCounter();
  }, [loadCounter]);

  const handleIncrement = async () => {
    setIsLoading(true);
    try {
      // Use default increment of 1
      const updatedCounter = await trpc.incrementCounter.mutate({
        increment: 1
      });
      setCounter(updatedCounter);
    } catch (error) {
      console.error('Failed to increment counter:', error);
    } finally {
      setIsLoading(false);
    }
  };

  if (isInitialLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardContent className="flex items-center justify-center p-8">
            <div className="text-lg text-gray-600">⏳ Loading...</div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-full max-w-md shadow-xl">
        <CardHeader className="text-center">
          <CardTitle className="text-3xl font-bold text-gray-800">
            🎯 Click Counter 🚀
          </CardTitle>
          <CardDescription className="text-gray-600">
            Click the button to increment the counter!
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Counter Display */}
          <div className="text-center">
            <div className="text-6xl font-bold text-indigo-600 mb-2">
              {counter?.count ?? 0}
            </div>
            <div className="text-sm text-gray-500">
              {counter?.updated_at && (
                <>Last updated: {counter.updated_at.toLocaleString()}</>
              )}
            </div>
          </div>

          {/* Increment Button */}
          <Button
            onClick={handleIncrement}
            disabled={isLoading}
            className="w-full h-14 text-lg font-semibold bg-indigo-600 hover:bg-indigo-700 disabled:opacity-50"
          >
            {isLoading ? (
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                ⏳ Incrementing...
              </div>
            ) : (
              <>
                ➕ Click Me! 🎉
              </>
            )}
          </Button>

          {/* Fun Stats */}
          <div className="grid grid-cols-2 gap-4 pt-4 border-t">
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-700">
                {counter?.count ? Math.floor(counter.count / 10) : 0}
              </div>
              <div className="text-xs text-gray-500">Tens</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-gray-700">
                {counter?.count ? counter.count % 10 : 0}
              </div>
              <div className="text-xs text-gray-500">Ones</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

export default App;

