import { afterEach, beforeEach, describe, expect, it } from 'bun:test';
import { resetDB, createDB } from '../helpers';
import { db } from '../db';
import { countersTable } from '../db/schema';
import { getCounter } from '../handlers/get_counter';

describe('getCounter', () => {
  beforeEach(createDB);
  afterEach(resetDB);

  it('should create and return a new counter when none exists', async () => {
    const result = await getCounter();

    // Verify default values
    expect(result.count).toEqual(0);
    expect(result.id).toBeDefined();
    expect(result.updated_at).toBeInstanceOf(Date);

    // Verify counter was actually saved to database
    const counters = await db.select()
      .from(countersTable)
      .execute();

    expect(counters).toHaveLength(1);
    expect(counters[0].count).toEqual(0);
    expect(counters[0].id).toEqual(result.id);
  });

  it('should return existing counter when one exists', async () => {
    // Create a counter with specific values
    const existingCounter = await db.insert(countersTable)
      .values({
        count: 42
      })
      .returning()
      .execute();

    const result = await getCounter();

    // Should return the existing counter, not create a new one
    expect(result.id).toEqual(existingCounter[0].id);
    expect(result.count).toEqual(42);
    expect(result.updated_at).toBeInstanceOf(Date);

    // Verify only one counter exists in database
    const counters = await db.select()
      .from(countersTable)
      .execute();

    expect(counters).toHaveLength(1);
  });

  it('should return first counter when multiple exist', async () => {
    // Create multiple counters
    await db.insert(countersTable)
      .values([
        { count: 10 },
        { count: 20 },
        { count: 30 }
      ])
      .execute();

    const result = await getCounter();

    // Should return first counter (lowest ID)
    expect(result.count).toEqual(10);
    expect(result.updated_at).toBeInstanceOf(Date);

    // Verify all counters still exist
    const counters = await db.select()
      .from(countersTable)
      .execute();

    expect(counters).toHaveLength(3);
  });
});
</file>

<file path="server/src/tests/increment_counter.test.ts">
import { afterEach, beforeEach, describe, expect, it } from 'bun:test';
import { resetDB, createDB } from '../helpers';
import { db } from '../db';
import { countersTable } from '../db/schema';
import { type IncrementCounterInput } from '../schema';
import { incrementCounter } from '../handlers/increment_counter';
import { eq } from 'drizzle-orm';

describe('incrementCounter', () => {
  beforeEach(createDB);
  afterEach(resetDB);

  it('should create new counter when none exists', async () => {
    const input: IncrementCounterInput = {
      increment: 5
    };

    const result = await incrementCounter(input);

    expect(result.id).toBeDefined();
    expect(result.count).toEqual(5);
    expect(result.updated_at).toBeInstanceOf(Date);
  });

  it('should increment existing counter', async () => {
    // Create initial counter
    const initialCounter = await db.insert(countersTable)
      .values({
        count: 10
      })
      .returning()
      .execute();

    const input: IncrementCounterInput = {
      increment: 3
    };

    const result = await incrementCounter(input);

    expect(result.id).toEqual(initialCounter[0].id);
    expect(result.count).toEqual(13); // 10 + 3
    expect(result.updated_at).toBeInstanceOf(Date);
    expect(result.updated_at.getTime()).toBeGreaterThan(initialCounter[0].updated_at!.getTime());
  });

  it('should use default increment value of 1', async () => {
    const input: IncrementCounterInput = {
      increment: 1
    };

    const result = await incrementCounter(input);

    expect(result.count).toEqual(1);
  });

  it('should handle large increment values', async () => {
    const input: IncrementCounterInput = {
      increment: 1000
    };

    const result = await incrementCounter(input);

    expect(result.count).toEqual(1000);
  });

  it('should save counter to database', async () => {
    const input: IncrementCounterInput = {
      increment: 7
    };

    const result = await incrementCounter(input);

    // Verify in database
    const counters = await db.select()
      .from(countersTable)
      .where(eq(countersTable.id, result.id))
      .execute();

    expect(counters).toHaveLength(1);
    expect(counters[0].count).toEqual(7);
    expect(counters[0].updated_at).toBeInstanceOf(Date);
  });

  it('should handle multiple increments correctly', async () => {
    // First increment
    const firstResult = await incrementCounter({ increment: 5 });
    expect(firstResult.count).toEqual(5);

    // Second increment
    const secondResult = await incrementCounter({ increment: 3 });
    expect(secondResult.id).toEqual(firstResult.id);
    expect(secondResult.count).toEqual(8); // 5 + 3

    // Third increment
    const thirdResult = await incrementCounter({ increment: 2 });
    expect(thirdResult.id).toEqual(firstResult.id);
    expect(thirdResult.count).toEqual(10); // 8 + 2
  });
});
</file>


