import { db } from '../db';
import { countersTable } from '../db/schema';
import { type Counter } from '../schema';

export const getCounter = async (): Promise<Counter> => {
  try {
    // Get the first counter (assuming single counter system)
    const result = await db.select()
      .from(countersTable)
      .limit(1)
      .execute();

    // If no counter exists, create one with default values
    if (result.length === 0) {
      const newCounter = await db.insert(countersTable)
        .values({
          count: 0
        })
        .returning()
        .execute();

      return newCounter[0];
    }

    return result[0];
  } catch (error) {
    console.error('Get counter failed:', error);
    throw error;
  }
};
